import React, { useState, useEffect } from 'react'
import { ReactDOM } from 'react-dom'
import List from './List'
import Alert from './Alert'

const getLocalStorage = () => {
  let list = localStorage.getItem('list')
  if(list) {
    return JSON.parse(localStorage.getItem('list'))
  } else {
    return []
  }
}

function App() {
  const [name, setName] = useState('')
  const [list, setList] = useState(getLocalStorage())
  const [isEditing, setIsEditing] = useState(false)
  const [editId, setEditId] = useState(null)
  const [alert, setAlert] = useState({show: false, msg: '', type: ''})
  const [status, setStatus] = useState('all')
  const [isActive, setIsActive] = useState('')

  const handleSubmit = e => {
    e.preventDefault()
    if (!name) {
      showAlert(true, 'Please enter value', 'red')
    } else if (name && isEditing) {
      setList(list.map((item) => {
        if(item.id === editId) {
          return {...item, title: name}
        }
        return item
      }))
      setName('')
      setEditId(null)
      setIsEditing(false)
      showAlert(true, 'Item edited', 'green')
    } else {
      showAlert(true, 'New item added', 'green')
      const newItem = { id: new Date().getTime().toString(), title: name }
      setList([...list, newItem])
      setName('')
    }
  }

  const showAlert = (show=false, msg='', type='') => {
    setAlert({show, msg, type})
  }

  const updateStatus = (selectedTask) => {
    let taskName = selectedTask.parentElement.lastElementChild
    if(selectedTask.checked) {
        taskName.classList.add('checked')
        list[selectedTask.id].status = 'completed'
    } else {
        taskName.classList.remove('checked')
        list[selectedTask.id].status = 'pending'
    }
    localStorage.setItem('list', JSON.stringify(list))
  }

  const clearList = () => {
     showAlert(true, 'Empty list', 'red')
     setList([])
  }

  const removeItem = (id) => {
    showAlert(true, 'Item removed', 'red')
    setList(list.filter((item) => item.id !== id))
  } 

  const editItem = (id) => {
    const specificItem = list.find((item) => item.id === id)
    setIsEditing(true)
    setEditId(id)
    setName(specificItem.title)
  }

  useEffect(() => {
    localStorage.setItem('list', JSON.stringify(list))
  }, [list])

  const showMenu = (id) => {
    setIsActive(id)
  }

  useEffect(() => {
    document.body.addEventListener('click', e => {
      setIsActive('')
    })
  }, [])

  return <section className='wrapper'>
    <h1>Task Manager</h1>
    <p className='alert'>{alert.show && <Alert {...alert} removeAlert={showAlert} list={list}/>}</p>
    <div className='task-input'>
      <input type="text" className='grocery' placeholder='Add a new task' value={name} onChange={(e) => setName(e.target.value)}/>
      <button type='submit' className='add-btn' onClick={handleSubmit}>
        {isEditing ? 'Edit' : 'Submit'}
      </button>
    </div>
    <div className="controls">
            <div className="filters">
                <span id="all" className="active">All</span>
                <span id="pending">Pending</span>
                <span id="completed">Completed</span>
            </div>
            <button className="clear-btn" onClick={clearList}>Delete all</button>
      </div>
    {list.length > 0 && (
      <List items={list} removeItem={removeItem} editItem={editItem} status={status} isActive={isActive} showMenu={showMenu}/>
    )}
  </section>
}

export default App
