import React from 'react'
import { FaEdit, FaTrash } from 'react-icons/fa'


const List = ({items, removeItem, editItem}) => {
  return <div className="task-box">
    {items.forEach((item, id) => {
      let isComplited = item.status === 'completed' ? 'checked' : ''
      if ("all" == item.status || 'all') {
        return (
        <li class="task">
          <label for={id}>
            <input onClick="updateStatus(this)" type="checkbox" id={id} {...isComplited}/>
            <p class='${isComplited}'>${item.name}</p>
          </label>
          <div class="settings">
            <img onClick="showMenu(this)" src="./icons/dots.png" alt="dots"/>
            <ul class="task-menu">
              <li onClick={removeItem}><img src="./icons/edit.png" alt="edit"/>Edit</li>
              <li onClick={editItem}><img src="./icons/delete.png" alt="delete"/>Delete</li>
            </ul>
          </div>
        </li>
        )
      }
    })}
  </div>
}

export default List
