import React from 'react'
import Settings from './Settings'

const List = ({items, removeItem, editItem, isActive, showMenu, updateStatus, isComplete}) => {
  return (
    items.map((item) => {
      const {id, title} = item
      return <li key={id} className="task">
        <label>
          <input onClick={updateStatus} type="checkbox" id={id}/>
          <p className={isComplete()}>{title}</p>
        </label>
        <Settings removeItem={removeItem} editItem={editItem} isActive={isActive} />
      </li>
    })
)}



export default List

