import React from 'react'

const Filter = ({filterItems, setFilteredList, list, setBtnState, btnState}) => {
    return (
    <div className="filters">
        <span onClick={() => {setFilteredList(list), setBtnState('all')}} id="all" className='active'>All</span>
        <span onClick={() => {filterItems('pending'), setBtnState('pending')}} id="pending" className='' >Pending</span>
        <span onClick={() => {filterItems('completed'), setBtnState('completed')}} id="completed" className=''>Completed</span>
    </div>
    )
}

export default Filter
