import React, { useEffect } from 'react'

const Alert = ({type, msg, removeAlert, list }) => {
  useEffect(() =>{
    const timeout = setTimeout(() => {
      removeAlert()
    }, 3000)
  }, [list])
  return {msg}
}

export default Alert
