import React from 'react'

const Filter = ({filterItems, setFilteredList, list, updateBtn, btnState}) => {
    return (
    <div className="filters">
        <span onClick={() => {setFilteredList(list), updateBtn('all')}} id="all" className={btnState.all}>All</span>
        <span onClick={() => {filterItems('pending'), updateBtn('pending')}} id="pending" className={btnState.pending}>Pending</span>
        <span onClick={() => {filterItems('completed'), updateBtn('completed')}} id="completed" className={btnState.completed}>Completed</span>
    </div>
    )
}

export default Filter
