import React from 'react'
import dots from './icons/dots.png'
import edit from './edit.png'
import deleteBtn from './delete.png'



const List = ({items, removeItem, editItem, status, isActive, showMenu}) => {
  return <ul className="task-box">
    {items.map((item) => {
      const {id, title} = item
      let isComplited = status === 'completed' ? 'checked' : ''
      if (status === 'all') {
        return (
        <li key={id} className="task">
          <label>
            <input type="checkbox" id={id} {...isComplited}/>
            <p className={isComplited}>{title}</p>
          </label>
          <div className="settings">
            <img onClick={() => showMenu(id)} src={dots} alt="dots"/>
            <ul className={`task-menu ${isActive === id ? 'show': ''}`} id={id}>
              <li onClick={() => editItem(id)}><img src={edit} alt="edit"/>Edit</li>
              <li onClick={() => removeItem(id)}><img src={deleteBtn} alt="delete"/>Delete</li>
            </ul>
          </div>
        </li>
        )
      }
    })}
  </ul>
}

export default List

