import React from 'react'
import { FaEdit, FaTrash } from 'react-icons/fa'


const List = ({items, removeItem, editItem, status}) => {
  return <ul className="task-box">
    {items.map((item) => {
      const {id, title} = item
      let isComplited = status === 'completed' ? 'checked' : ''
      if (status === 'all') {
        return (
        <li className="task">
          <label for={id}>
            <input type="checkbox" id={id} {...isComplited}/>
            <p className={isComplited}>{title}</p>
          </label>
          {/* <div className="settings">
            <img onClick="showMenu(this)" src="./icons/dots.png" alt="dots"/>
            <ul className="task-menu">
              <li onClick={() => removeItem(id)}><img src="./icons/edit.png" alt="edit"/>Edit</li>
              <li onClick={() => editItem(id)}><img src="./icons/delete.png" alt="delete"/>Delete</li>
            </ul>
          </div> */}
        </li>
        )
      }
    })}
  </ul>
}

export default List

